function Game(){this.answer=Math.ceil(100*Math.random()),this.numGuessesLeft=5,this.guessElement=$("#guessbox"),this.numGuessesLeftNode=$("#num-guesses"),this.updateNumGuessesDisplay()}Game.prototype.retrieveGuess=function(){return console.log(this.guessElement.val()),this.guessElement.val()},Game.prototype.correctGuess=function(){return this.retrieveGuess()==this.answer},Game.prototype.updateNumGuessesDisplay=function(){this.numGuessesLeftNode.text(this.numGuessesLeft)},Game.prototype.minusGuess=function(){return this.numGuessesLeft--,this.updateNumGuessesDisplay(),0==this.numGuessesLeft?this.lose():void 0},Game.prototype.turn=function(){return this.correctGuess()?this.win():void this.minusGuess()},Game.prototype.win=function(){alert("YOU WIN!")},Game.prototype.lose=function(){alert("YOU LOSE! The right answer was "+this.answer)},$(document).ready(function(){var e=new Game;$("#submit-guess").on("click",function(s){s.preventDefault(),e.turn()}),$("#restart").on("click",function(s){s.preventDefault(),e=new Game})});